INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (1,'Conservation should focus on preventing deterioration rather than substituting existing components','2025-08-11 16:59:26','2025-08-11 16:59:26');
INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (2,'Use traditional materials for maintenance, repairs, and reinforcement to minimize nonreversible modifications','2025-08-11 16:59:55','2025-08-11 16:59:55');
