INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (4,'Building modifications are acceptable if they serve purposes like continued family use, accessibility, or agriculture requirements. Any changes should be in line with the vernacular character of the village','2025-08-11 17:02:53','2025-08-11 17:02:53');
INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (5,'Volume additions must match the current footprint, height, and stories. The new additions should be clearly distinguishable from the original building and designed to visually complement it','2025-08-11 17:02:59','2025-08-11 17:02:59');
INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (6,'Substitute nontraditional tiles with stone slate tiles','2025-08-11 17:03:03','2025-08-11 17:03:03');
INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (7,'Substitute nontraditional doors and windows with wooden ones','2025-08-11 17:03:07','2025-08-11 17:03:07');
INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (8,'Preserve existing vernacular components','2025-08-11 17:03:11','2025-08-11 17:03:11');
INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (9,'Minimize the impact of existing alterations to restore part of the buildings’ cultural significance','2025-08-11 17:03:15','2025-08-11 17:03:15');
INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (10,'Nontraditional materials can be used for maintenance, repairs, and reinforcement in nonvernacular components, provided they don’t damage existing vernacular elements','2025-08-11 17:03:23','2025-08-11 17:03:23');
INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (11,'Building modifications are acceptable if they conserve existing vernacular components. Any changes should be in line with the vernacular character of the village','2025-08-11 17:03:30','2025-08-11 17:03:30');
INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (12,'Conservation efforts must adhere to the “principle of minimal intervention”, unless a proposed intervention provides demonstrable community benefit','2025-08-11 17:03:37','2025-08-11 17:03:37');
