INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (14,'Musealization can be implemented if the intention is to highlight the building''s historical role in the community','2025-08-11 17:04:48','2025-08-11 17:04:48');
INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (15,'Preserve the building''s historical use as much as feasible; explore other uses if they demonstrably improve community value','2025-08-11 17:05:25','2025-08-11 17:05:25');
INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (16,'Use reversible and traditional methods to stabilize and prevent collapse, where possible.','2025-08-11 17:05:37','2025-08-11 17:05:37');
INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (17,'Implement measures to reduce water infiltration and damage to the ruins','2025-08-11 17:05:41','2025-08-11 17:05:41');
INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (18,'Assess state of ruins to determine if they can be safely adaptable for public use','2025-08-11 17:05:46','2025-08-11 17:05:46');
INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (19,'Reconstruct using traditional methods and materials based on historical evidence','2025-08-11 17:05:50','2025-08-11 17:05:50');
INSERT INTO conservation (id,description,createdAt,updatedAt) VALUES (20,'Additions or adaptations may be considered if exact reconstruction isn''t possible or functional requirements are needed, as long as they''re clearly justified and do not compromise the village authenticity','2025-08-11 17:06:38','2025-08-11 17:06:38');
